using Negocio.Utilidades;using PersistenciaSigeor;using System;using System.Collections.Generic;using System.Diagnostics;using System.Linq;using System.Transactions;namespace Negocio.Sigeor.GestionControl{    public class NegociacionLineaHistoricoNegocio    {        public static string ObtenerNegociacionesLineaHistoricoPaginado(string parametro, int pagesize, int pageIndex, out int totalRegistros)        {            List<GET_HISTORIAL_NEG_LINEAS_Result> result;            try            {                var param = Serializador.DeSerializeEntity<ClaseBasica>(parametro);                var transactionOptions = new TransactionOptions                {                    IsolationLevel = System.Transactions.IsolationLevel.ReadUncommitted                };                using (var transactionScope = new TransactionScope(TransactionScopeOption.Required, transactionOptions))                {                    using (var context = new SigeorEntities())                    {                        result = context.GET_HISTORIAL_NEG_LINEAS(param.FechaDateTimeStringUno, param.FechaDateTimeStringDos, param.Descripcion, param.EstadoUno, param.EstadoString).ToList();                    }                    transactionScope.Complete();                }                totalRegistros = result.Count;                result = result.Skip(pagesize * (pageIndex-1)).Take(pagesize).ToList();            }            catch (Exception ex)            {                Log.WriteEntry("No se pudo obtener el historico de las negociaciones por linea existentes paginado: " + ex, EventLogEntryType.Error);                throw new Exception("No se pudo obtener el historico de las negociaciones por linea existentes paginado: " + ex);            }            return result.Any() ? Serializador.SerializeEntity(result) : null;        }        public static string ObtenerNegociacionesLineaHistorico(string parametro)        {            List<GET_HISTORIAL_NEG_LINEAS_Result> result;            try            {                var param = Serializador.DeSerializeEntity<ClaseBasica>(parametro);                var transactionOptions = new TransactionOptions                {                    IsolationLevel = System.Transactions.IsolationLevel.ReadUncommitted                };                using (var transactionScope = new TransactionScope(TransactionScopeOption.Required, transactionOptions))                {                    using (var context = new SigeorEntities())                    {                        result = context.GET_HISTORIAL_NEG_LINEAS(param.FechaDateTimeStringUno, param.FechaDateTimeStringDos, param.Descripcion, param.EstadoUno, param.EstadoString).ToList();                    }                    transactionScope.Complete();                }            }            catch (Exception ex)            {                Log.WriteEntry("No se pudo obtener el historico de las negociaciones por linea existentes: " + ex, EventLogEntryType.Error);                throw new Exception("No se pudo obtener el historico de las negociaciones por linea existentes: " + ex);            }            return result.Any() ? Serializador.SerializeEntity(result) : null;        }        #region Obtener Negociacion por Linea        public static GET_NEGOCIACION_LINEA_X_FECHA_Result ObtenerNegociacionLineaPorFecha(ClaseBasica parametros)        {            GET_NEGOCIACION_LINEA_X_FECHA_Result result = null;            try            {                var transactionOptions = new TransactionOptions                {                    IsolationLevel = IsolationLevel.ReadUncommitted                };                using (var transactionScope = new TransactionScope(TransactionScopeOption.Required, transactionOptions))                {                    using (var context = new SigeorEntities())                    {                        result = context.GET_NEGOCIACION_LINEA_X_FECHA(                            parametros.IdStringUno,                            parametros.IdStringTres,                            parametros.FechaDateTimeUno).FirstOrDefault();                    }                    transactionScope.Complete();                }            }            catch                (Exception ex)            {                Log.WriteEntry("No se pudo buscar la negociacion por linea en el rango de fechas dado: " + ex,                    EventLogEntryType.Error);            }            return result;        }        #endregion Obtener Negociacion por Linea    }}
