using System;using System.Collections.Generic;using System.Diagnostics;using System.Linq;using System.Text;using PersistenciaSigeor;namespace Negocio.Utilidades{    public class DatosCreados    {
        /// <summary>        ///  Genera los tipos de datos partiendo de una clase base del sistema        /// </summary>        /// <returns>Serializado de Lista de Danios</returns>        public static string CargarTipoDanios()        {            var listaTipoDanios = new List<ClaseBasica>            {                new ClaseBasica { IdString = "BA", Nombre = "TIPO BARRIDO" },                new ClaseBasica { IdString = "LV", Nombre = "TIPO LAVADO" },                new ClaseBasica { IdString = "RP", Nombre = "TIPO REEMPLAZO" }            };            return Serializador.SerializeEntity(listaTipoDanios.OrderBy(ent => ent.Nombre).ToList());        }

        public static string CargarTipoAlertas()        {            var listaTipoDanios = new List<ClaseBasica>            {                new ClaseBasica { IdGuid = Guid.Parse("82DAD262-7113-4537-8131-785D7A83ED8F"), Nombre = "BAJA" },                new ClaseBasica { IdGuid = Guid.Parse("A66CBEF9-C6F2-4C1F-9401-418364322CB4"), Nombre = "MEDIA" },                new ClaseBasica { IdGuid = Guid.Parse("90df54d2-cbcb-463b-ae64-9420489321c2"), Nombre = "ALTA" }            };            return Serializador.SerializeEntity(listaTipoDanios);        }

        
        /// <summary>        /// Obtiene los estados de los Eor de Estructura y Maquinaria.        /// </summary>        /// <returns>Lista de estados</returns>        public static string ObtenerEstadosEorEstructuraMaquinaria()        {            List<ClaseBasica> listaEstados;            try            {                listaEstados = new List<ClaseBasica>                {                    new ClaseBasica{IdString = "D", Nombre = "DAÑADO", Descripcion = "DAÑADO"},                    new ClaseBasica{IdString = "A", Nombre = "APROBADO", Descripcion = "APROBADO"},                    new ClaseBasica{IdString = "R", Nombre = "REPARADO", Descripcion = "REPARADO"}                };            }            catch (Exception ex)            {                Log.WriteEntry("No se pudo cargar los estados de los Eor Estructura y Maquinaria : " + ex, EventLogEntryType.Error);                throw new Exception("No se pudo cargar los estados de los Eor Estructura y Maquinaria : " + ex);            }            return listaEstados.Any() ? Serializador.SerializeEntity(listaEstados) : null;        }        public static string CargarEstadosNegociaciones()        {            List<ClaseBasica> listaEstados;            try            {                listaEstados = new List<ClaseBasica>                {                    new ClaseBasica{IdString = "1", Nombre = "ACTIVOS", Descripcion = "Activos"},                    new ClaseBasica{IdString = "0", Nombre = "INACTIVOS", Descripcion = "Inactivos"},                    new ClaseBasica{IdString = string.Empty, Nombre = "TODOS", Descripcion = "Todos"}                };            }            catch (Exception ex)            {                Log.WriteEntry("No se pudo cargar los estados de las negociaciones: " + ex, EventLogEntryType.Error);                throw new Exception("No se pudo cargar los estados de las negociaciones: " + ex);            }            return listaEstados.Any() ? Serializador.SerializeEntity(listaEstados) : null;        }    }}