using System;using System.Collections.Generic;using System.Globalization;using System.Linq;using System.Text;using System.IO;using System.Xml.Serialization;namespace Negocio.Utilidades{    public class Serializador    {        public static string SerializeEntity<T>(T source)        {            var serializer = new XmlSerializer(source.GetType());            using (StringWriter textWriter = new StringWriter())            {                serializer.Serialize(textWriter, source);                return textWriter.ToString();            }        }        public static T DeSerializeEntity<T>(string xml)        {            var serializer = new XmlSerializer(typeof(T));            return (T)serializer.Deserialize(new StringReader(xml));        }    }}
